    private void itemLocalidadesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_itemLocalidadesActionPerformed
        Localidad ventanaLocalidades = new Localidad(cnx);
        if(cnx != null)
        {   
            desktopPane.add(ventanaLocalidades);
            ventanaLocalidades.setVisible(true);
        }
        else
            JOptionPane.showMessageDialog(null, "Sin Conexion!!...");
    }//GEN-LAST:event_itemLocalidadesActionPerformed

